<?php class Controller_Admin_Index_Home extends ypAdminController {	public function Index() {		$this->checkLogin();		$this->loadLanguage('Admin/Index/Home');				$this->Loader->model('Admin/Index/Home');		$this->set('news', $this->Model_Admin_Index_Home->loadNewsPost(4));				$this->set('link', array(			'setting' => array(				'index' => $this->Link->build('Admin/Setting/Index'),				'site' => $this->Link->build('Admin/Setting/Site'),				'server' => $this->Link->build('Admin/Setting/Server'),				'user' => $this->Link->build('Admin/Setting/User'),				),			'newpost' => $this->Link->build('Admin/News/Newpost'),			'addpage' => $this->Link->build('Admin/Page/Newpage'),			'front' => $this->Link->build('/'),			));		$this->set('stat', array(			'pageview' => number_format($this->Setting->get('pageview')),			'totalpost' => number_format($this->Setting->get('totalpost')),			'totaldraft' => number_format($this->Setting->get('totaldraft')),			'comment' => 256, // fix later			));				// ===============		$this->setTemplate('module/Index/Home.php');		$this->Document->setTitle($this->_data['title']);		$this->setTemplateChild(array(			'Admin/System/Header',			'Admin/System/Footer'		));		$render = $this->render();		$this->Response->setOutput($render);	}}