<?php class Controller_Admin_Module_Home_Index extends ypAdminController {	public function Index() {		// Check Admin		$this->Loader->model('Admin/Admin/Admin');		if (!$this->Model_Admin_Admin_Admin->isLogin()) {			$this->redirect($this->Link->build('Admin/Login/Index', TRUE));		}		$this->Document->set('is_login', TRUE);				$this->loadLanguage('Admin/Module/Home/Index');		$this->set('error', '');		$this->set('success', '');		if ($this->Request->POST['content']) {			$content = htmlspecialchars($this->Request->POST['content']);		}				die('Update later...........');	}}