<?php class Controller_Admin_News_Delete extends ypAdminController {	public function Index() {		$this->checkLogin();		$postId = intval($this->Request->GET['id']);		if ($postId == 0) {			$this->redirect($this->Link->build('Admin/News/Newpost'));		}		if ($this->Request->REQUEST['submit']) {			$this->Loader->model('Admin/News/Delete');			$this->Model_Admin_News_Delete->deletePost($postId);			// Reload cache			$this->Cache->delete('postInIndex');		} else {			// Xac nhan xoa		}		$this->set('redirect_to', $this->Link->build('Admin/News/Listpost'));		$this->setTemplate('layout/Redirect.php');		$this->Response->setOutput($this->render());	}}