<?php class Controller_Admin_Setting_Menu extends ypAdminController {	public function Index() {		$this->checkLogin();				$menu = $this->Setting->get('menu');		print_r($menu);die;				$this->loadLanguage('Admin/Setting/Menu');		$this->Document->setTitle($this->_data['title']);		$this->setTemplate('module/Setting/Menu.php');		$this->setTemplateChild(array(			'Admin/System/Header',			'Admin/System/Footer'		));		$this->Response->setOutput($this->render());	}}