<?php class Controller_Admin_Setting_SettingLeftMenu extends ypAdminController {	public function Index() {		$this->checkLogin();				$this->loadLanguage('Admin/System/Header');				$menu[] = $this->Link->a('Admin/Setting/Site', $this->_data['text_menu_site_setting']);		$menu[] = $this->Link->a('Admin/Setting/UrlAndHtml', $this->_data['text_menu_urlandhtml_setting']);		$menu[] = $this->Link->a('Admin/Setting/Server', $this->_data['text_menu_server_setting']);		$menu[] = $this->Link->a('Admin/Setting/Template', $this->_data['text_menu_template_setting']);		$menu[] = $this->Link->a('Admin/Setting/Security', $this->_data['text_menu_security_setting']);		$menu[] = $this->Link->a('Admin/Setting/User', $this->_data['text_menu_user_setting']);				$this->set('menu', $menu);				$this->setTemplate('module/Setting/SettingLeftMenu.php');		$this->render();	}}