<?php class Model_Admin_News_Progress extends ypModel {	public function addPost($data) {		if ($data['post_id']) {			return $this->editPost($data);		}		if (!$this->Db->query("INSERT INTO `yp_module_news` (`title`, `title_alias`, `post`, `tag`, `keyword`, `user_id`, `username`, `post_date`, `status`, `editor`) 		VALUES ('". $this->Db->e($data['title']) ."', '". $this->Db->e($data['title_alias']) ."', '". $this->Db->e($data['post']) ."', '". $this->Db->e($data['tag']) ."', '". $this->Db->e($data['keyword']) ."',". (int) $data['user']['user_id'] .", '". $this->Db->e($data['user']['username']) ."', ". time() .", $data[save], '". $this->Db->e($data['editor']) ."')")) {			$this->Db->fatalError();			//return FALSE;		}		return $this->Db->insertID();	}		public function editPost($data) {		if (!$this->Db->query("UPDATE `yp_module_news` 		SET 			`title` = '". $this->Db->e($data['title']) ."', 			`title_alias` = '". $this->Db->e($data['title_alias']) ."', 			`post` = '". $this->Db->e($data['post']) ."', 			`keyword` = '". $this->Db->e($data['keyword']) ."', 			`user_id` = '". (int) $data['user']['user_id'] ."', 			`username` = '". $this->Db->e($data['user']['username']) ."', 			`time_last_change` = ". time() .",			`tag` = '". $this->Db->e($data['tag']) ."',			`status` = $data[save],			`editor` = '". $this->Db->e($data['editor']) ."'		WHERE `post_id` = ". (int) $data['post_id'])) {			return FALSE;		}		return $data['post_id'];	}}