<?php class Model_Admin_Tools_Backup extends ypModel {	public function getTable() {		$this->Db->query("SHOW TABLES LIKE 'yp_%'");		$tables = array();		while ($row = $this->Db->fetch()) {			$table = array_values($row);			$tables[] = trim($table[0]);		}				return $tables;	}	public function getTableStatus() {		$_table = $this->getTable();		if (empty($_table)) {			return FALSE;		}		$table = array();		$i = 0;		$this->Db->query("SHOW TABLE STATUS");		while ($row = $this->Db->fetch()) {			print_r($row);die;		}	}	public function getDatabaseTable() {		$_table = $this->getTable();		if (empty($_table)) {			return FALSE;		}		$table = array();		$i = 0;		$this->Db->query("SHOW TABLE STATUS");		while ($row = $this->Db->fetch()) {			if (in_array($row['Name'], $_table)) {				$table[$i]['name'] = $row['Name'];				$table[$i]['rows'] = $row['Rows'];				$table[$i]['collation'] = $row['Collation'];				$i++;			}		}				return $table;	}	public function repairTable($table) {		$table = (string) $table;		return $this->Db->query('REPAIR TABLE  `'. $table .'`');	}		public function optimizeTable($table) {		$table  = (string) $table;				return $this->Db->query('OPTIMIZE TABLE  `'. $table .'`');	}}