{$Header}<main role="main" id="main">	<aside id="notifications">		<!-- Notifications -->		{if !empty($error)}<div class="notification-error">{$error}</div>{/if}		{if !empty($success)}<div class="notification-success">{$success}</div>{/if}	</aside>	<section class="content-view-container">	    <section class="content-list js-content-list">	      <header class="floatingheader">	          <section class="content-filter">	              <span>All Posts</span>	          </section>	          <a href="{$newpost_link}" class="button button-add"><span class="hidden">New Post</span></a>	      </header>	      <section class="content-list-content">			<ol>				{foreach $POSTS as $post}					<li class="each-post" data-toggle="tooltip" data-placement="bottom" title="{if $post.tag}Post in tags: {$post.tag}{else}No tag.{/if}">						<h3 class="entry-title">							<a class="permalink" href="{$post.link_edit}" >{$post.title} {if $post.status != 'publish'}<span class="draft">[{$post.status}]</span>{/if}</a>							<span class="meta-post-right">								<a href="{$post.link_viewpost}" target="_blank" class="viewpost">View post</a> 								<time datetime="{$post.datetime}" class="date">{$post.datetime}</time>							</span>						</h3>					</li>				{/foreach}			</ol>	      </section>	    </section>	</section></main><script type="text/javascript" src="{$static_url}/javascript/jquery/jquery-1.9.1.js"></script><script type="text/javascript" src="{$static_url}/javascript/bootstrap/bootstrap-tooltip.js"></script><script type="text/javascript">$('.each-post').tooltip();</script></body></html>