{$Header}<main role="main" id="main">	<aside id="notifications">		<!-- Notifications -->		{if !empty($error)}<div class="notification-error">{$error}<a href="#" class="close"></a></div>{/if}		{if !empty($success)}<div class="notification-success">{$success}<a href="#" class="close"></a></div>{/if}	</aside>	<div class="wrapper">		{$Leftmenu}				<section class="settings-content active" id="general" style="display: block;">			<header>				<button class="button-back">Back</button>				<h2 class="title">{$text_pagetitle}</h2>				<section class="page-actions">									</section>			</header>			<section class="content">				<form id="settings-general" novalidate="novalidate" action="{$form_action}" method="post">					<div class="form-group">		                <label for="template">{$text_site_template}</label>		                <select name="template">						{foreach $template as $_template}							<option value="{$_template.dir}" {if ($_template.dir == $current_template)}selected="selected"{/if}>{$_template.name}</option>						{/foreach}						</select>		                <p></p>		            </div>		            <div class="form-group">		                <label for="admin_template">{$text_admin_template}</label>		                <select name="admin_template" >						{foreach $admin_template as $_template}							<option value="{$_template.dir}" {if ($_template.dir == $current_admin_template)}selected="selected"{/if}>{$_template.name}</option>						{/foreach}						</select>		                <p></p>		            </div>		            <div class="form-group">		                <label for="cache_template">{$text_cache_template}</label>						<input type="radio" name="cache_template" value="1" {if $cache_template == '1'} checked="checked"{/if} /> {$text_on} 						<input type="radio" name="cache_template" value="0" {if $cache_template == '0'} checked="checked"{/if} /> {$text_off}<br />		                <p></p>		            </div>		            <section class="page-actions">						<button type="submit" name="submit" class="button-save">{$button_save_setting}</button>					</section>				</form>			</section>		</section>	</div></main>{$Footer}