{assign var=body_class value="settings"}{$Header}<main role="main" id="main">	<aside id="notifications">		<!-- Notifications -->		{if !empty($error)}<div class="notification-error">{$error}<a href="#" class="close"></a></div>{/if}		{if !empty($success)}<div class="notification-success">{$success}<a href="#" class="close"></a></div>{/if}	</aside>	<div class="wrapper">		{$Leftmenu}				<section class="settings-content active" id="general" style="display: block;">			<header>				<button class="button-back">Back</button>				<h2 class="title">{$text_pagetitle}</h2>				<section class="page-actions">									</section>			</header>			<section class="content">			    <form id="settings-general" novalidate="novalidate" action="{$form_action}" method="post">			    	<fieldset>			    		<div class="form-group">			                <label for="compress_page">{$text_setting_compresspage}</label>				                <input type="radio" name="compress_page" value="1" {if $settings.compress_page == '1'} checked="checked"{/if} /> {$text_on}				                <input type="radio" name="compress_page" value="0" {if $settings.compress_page == '0'} checked="checked"{/if} /> {$text_off}						        <p></p>			            </div>			            <div class="form-group">			                <label for="compression_level">{$text_setting_compression_level}</label>			                <input id="tmp" name="compression_level" type="text" value="{$settings.compression_level}">			                <p></p>			            </div>			            <div class="form-group">			                <label for="compress_html">{$text_setting_compress_html}</label>				                <input type="radio" name="compress_html" value="1" {if $settings.compress_html == '1'} checked="checked"{/if} /> {$text_on}<br />				                <input type="radio" name="compress_html" value="0" {if $settings.compress_html == '0'} checked="checked"{/if} /> {$text_off}<br />						        <p></p>			            </div>			             <div class="form-group">			                <label for="rewrite">{$text_setting_rewrite}</label>				                <input type="radio" name="rewrite" value="1" {if $settings.rewrite == '1'} checked="checked"{/if} /> {$text_on}<br />				                <input type="radio" name="rewrite" value="0" {if $settings.rewrite == '0'} checked="checked"{/if} /> {$text_off}<br />						        <p></p>			            </div>			             <div class="form-group">			                <label for="google_analytics_code">{$text_setting_google_analytics_code}</label>			                <input id="tmp" name="google_analytics_code" type="text" value="{$settings.google_analytics_code}">			                <p></p>			            </div>			            <div class="form-group">			                <label for="url_lowcase">{$text_setting_url_lowcase}</label>				                <input type="radio" name="url_lowcase" value="1" {if $settings.url_lowcase == '1'} checked="checked"{/if} /> {$text_on}<br />				                <input type="radio" name="url_lowcase" value="0" {if $settings.url_lowcase == '0'} checked="checked"{/if} /> {$text_off}<br />						        <p><i>index/home</i> instead <i>Index/Home</i></p>			            </div>			            <div class="form-group">			                <label for="static_url_active">{$text_setting_static_url_active}</label>				                <input type="radio" name="static_url_active" value="1" {if $settings.static_url_active == '1'} checked="checked"{/if} /> {$text_on}<br />				                <input type="radio" name="static_url_active" value="0" {if $settings.static_url_active == '0'} checked="checked"{/if} /> {$text_off}<br />						        <p></p>			            </div>			            <div class="form-group">			                <label for="static_url">{$text_setting_static_url}</label>			                <input id="tmp" name="static_url" type="text" value="{$settings.static_url}">			                <p></p>			            </div>			        </fieldset>			        <section class="page-actions">						<button type="submit" name="submit" class="button-save">Save</button>					</section>			    </form>			</section>		</section>	</div></main>{$Footer}