{$Header}<main role="main" id="main">	<aside id="notifications">		<!-- Notifications -->		{if !empty($error)}<div class="notification-error">{$error}<a href="#" class="close"></a></div>{/if}		{if !empty($success)}<div class="notification-success">{$success}<a href="#" class="close"></a></div>{/if}	</aside>	<div class="wrapper">		{$Leftmenu}				<section class="settings-content active" id="general" style="display: block;">			<header>				<button class="button-back">Back</button>				<h2 class="title">{$text_pagetitle}</h2>				<section class="page-actions">									</section>			</header>			<section class="content">			    <form id="settings-general" novalidate="novalidate" action="{$form_action}" method="post">			    	<fieldset>			    		<div class="form-group">			                <label for="username_maxchars">{$text_setting_username_maxchars}</label>			                <input id="tmp" name="username_maxchars" type="text" value="{$settings.username_maxchars}">			                <p></p>			            </div>			            <div class="form-group">			                <label for="username_minchars">{$text_setting_username_minchars}</label>			                <input id="tmp" name="username_minchars" type="text" value="{$settings.username_minchars}">			                <p></p>			            </div>			            <div class="form-group">			                <label for="password_maxchars">{$text_setting_password_maxchars}</label>			                <input id="tmp" name="password_maxchars" type="text" value="{$settings.password_maxchars}">			                <p></p>			            </div>			            <div class="form-group">			                <label for="password_minchars">{$text_setting_password_minchars}</label>			                <input id="tmp" name="password_minchars" type="text" value="{$settings.password_minchars}">			                <p></p>			            </div>			            <div class="form-group">			                <label for="register_active">{$text_setting_register_active}</label>				                <input type="radio" name="register_active" value="1" {if $settings.register_active == '1'} checked="checked"{/if} /> {$text_on}				                <input type="radio" name="register_active" value="0" {if $settings.register_active == '0'} checked="checked"{/if} /> {$text_off}						        <p></p>			            </div>			            <div class="form-group">			                <label for="facebook_login">{$text_setting_facebook_login}</label>				                <input type="radio" name="facebook_login" value="1" {if $settings.facebook_login == '1'} checked="checked"{/if} /> {$text_on}				                <input type="radio" name="facebook_login" value="0" {if $settings.facebook_login == '0'} checked="checked"{/if} /> {$text_off}						        <p></p>			            </div>			            <div class="form-group">			                <label for="google_login">{$text_setting_google_login}</label>				                <input type="radio" name="google_login" value="1" {if $settings.google_login == '1'} checked="checked"{/if} /> {$text_on}				                <input type="radio" name="google_login" value="0" {if $settings.google_login == '0'} checked="checked"{/if} /> {$text_off}						        <p></p>			            </div>			        </fieldset>			        <section class="page-actions">						<button type="submit" name="submit" class="button-save">{$button_save_setting}</button>					</section>			    </form>			</section>		</section>	</div></main>{$Footer}