<?php class Controller_Register_Index extends ypController {	public function Index() {		$this->loadLanguage('Register/Index');		$this->Document->setTitle($this->_data['title']);				$this->set('redirect_to', '');		$this->set('form_action', $this->Link->build('Register/Progress'));				$this->set('error', '');		$this->set('username', '');		$this->set('password', '');		$this->set('re_password', '');		$this->set('full_name', '');		$this->set('email', '');				$this->setTemplate('module/Register/Form.php');		$this->setTemplateChild(array(			'System/Header',			'System/Footer',		));		$this->Response->setOutput($this->render());	}}