<?php class Controller_System_Captcha extends ypController {	public function Index() {		$param['width'] =  isset($this->Request->GET['width']) ? intval($this->Request->GET['width']) : 80;		$param['height'] = isset($this->Request->GET['height']) ? intval($this->Request->GET['height']) : 30;		$param['strlen'] = isset($this->Request->GET['strlen']) ? intval($this->Request->GET['strlen']) : 5;		$param['session_name'] = isset($this->Request->GET['session_name']) ? trim($this->Request->GET['session_name']) : 'captcha_code';				$this->Loader->helper('captcha');		ypCaptcha($param);	}}