<?php class Controller_System_NotFound extends ypController {	public function index() {		// Log 404 error 		$link = $this->Link->build($this->Link->this(), FALSE, $this->Link->getParams());		$this->Loader->helper('log');		ypLog('['. date('h:i:s') .'] #F404 :: Error 404 :: ' . $link . ' - IP :: ' . long2ip($this->Request->ip_addr) . ' - UserAgent :: ['. $this->Request->userAgent .']');				$this->loadLanguage('System/404');		$this->setTemplate('layout/404.php');		$this->Response->setOutput($this->render());	}}