<?php class Controller_User_Logout extends ypController {	public function Index() {		$this->Session->delete('userName');		$this->Session->delete('userLoginHash');		$this->Session->set('isLogin', FALSE);		$this->Cookie->delete('userName');		$this->Cookie->delete('userLoginHash');		$redirect = urldecode($this->Request->GET['redirect']);		if (!$redirect) {			$redirect = $this->Link->build('Index/Home', TRUE);		}		$this->loadLanguage('User/Logout');		$this->setTemplate('module/User/Logout.php');		$this->set('redirect_to', $redirect);		$this->Response->setOutput($this->render());	}}