<?php class Controller_User_Logout extends ypController {	public function Index() {		$deleteThis = array('username', 'time_login', 'client', 'isAdmin', 'hashkey');		$this->Session->delete($deleteThis);		$this->Cookie->delete($deleteThis);		$this->Loader->model('User/Logout');		$this->Model_User_Logout->deleteClientSession($this->User->get('hashkey'));		$redirect = urldecode($this->Request->GET['redirect']);		if (!$redirect) {			$redirect = $this->Link->build('Index/Home', TRUE);		}		$this->loadLanguage('User/Logout');		$this->setTemplate('module/User/Logout.php');		$this->set('redirect_to', $redirect);		$this->Response->setOutput($this->render());	}}