<?php class Model_Login_Progress extends ypModel {	public function GetUserInfo($username) {		if (empty($username)) return FALSE;		$this->Loader->helper('Email');		if (isEmail($username)) {			$this->Db->query("SELECT * FROM `yp_users` WHERE `email` = '". $this->Db->e($username) . "'");		} else {			$this->Db->query("SELECT * FROM `yp_users` WHERE `username` = '". $this->Db->e($username) . "'");		}		if ($this->Db->num_rows() == 0) return FALSE;		return $this->Db->fetch();	}	public function setSessionClient($login = array()) {		return $this->Db->query("INSERT INTO `yp_sessions` (`username`, `hash`, `last_time`, `client`, `is_admin`) 			VALUES ('". $this->Db->e($login['username']) ."', '". $this->Db->e($login['hashkey']) ."', $login[time_login], '". $this->Db->e($login['client']) ."', '". $this->Db->e($login['isAdmin']) ."')");	}}