<form action="{$form_action}" method="POST">	<div class="control-group">		<h3>{$text_comment}</h3>		<div class="comment-status">		{if $error}<div class="alert-error ">{$error}</div>{/if}		{if $success}<div class="alert-success">{$success}</div>{/if}		</div>		 {if ($is_login == TRUE)}		   <blockquote class="">		     <p>{$text_username}: {if !empty($user.full_name)}{$user.full_name}{else}{$user.username}{/if}</p>		     <p>{$text_email}: {$user.email}</p>		   </blockquote>		 {else}		   <input type="text" name="name" class="input-block-level input-text" value="" placeholder="{$text_placeholder_username}">		   <input type="text" name="email" class="input-block-level input-text" value="" placeholder="{$text_placeholder_email}">		 {/if}		 {if ($captcha)}		   <img src="{$captcha_img_url}" alt="" title="" />		   <input type="text" name="captcha" class="input-block-level input-text" value="" placeholder="{$text_placeholder_captcha}" style="width: 50%" />		 {/if}		 <input type="hidden" name="post_id" value="{$post_id}" /> 		 <input type="hidden" name="is_login" value="{if $is_login}true{else}false{/if}" /> 		 <textarea rows="5" cols="20" class="input-block-level" name="comment_content" id="comment_content" placeholder="{$text_placeholder_comment}"></textarea>		 		 <button name="submit" value="1" class="comment-button">{$text_button_comment}</button> 		 {if (!$is_login)} {$text_login_after_button}{/if}	</div></form>