<form action="<?php echo $form_action ?>" method="POST">	<div class="control-group"><h3><?php echo $text_comment ?></h3><div class="comment-status"><?php if ($error) { ?><div class="alert-error "><?php echo $error ?></div><?php } ?><?php if ($success) { ?><div class="alert-success"><?php echo $success ?></div><?php } ?></div> <?php if ($is_login == TRUE) { ?>   <blockquote class="">     <p><?php echo $text_username ?>: <?php echo !empty($user['full_name']) ? $user['full_name'] : $user['username'] ?></p>     <p><?php echo $text_email ?>: <?php echo $user['email'] ?></p>   </blockquote> <?php } else { ?>   <input type="text" name="name" class="input-block-level input-text" value="" placeholder="<?php echo $text_placeholder_username ?>">   <input type="text" name="email" class="input-block-level input-text" value="" placeholder="<?php echo $text_placeholder_email ?>"> <?php } ?> <?php if ($captcha) { ?>   <img src="<?php echo $captcha_img_url ?>" alt="" title="" />   <input type="text" name="captcha" class="input-block-level input-text" value="" placeholder="<?php echo $text_placeholder_captcha ?>" style="width: 50%" /> <?php } ?> <input type="hidden" name="post_id" value="<?php echo $post_id ?>" />  <input type="hidden" name="is_login" value="<?php echo $is_login ? 'true' : 'false' ?>" />  <textarea rows="5" cols="20" class="input-block-level" name="comment_content" id="comment_content" placeholder="<?php echo $text_placeholder_comment ?>"></textarea>  <button name="submit" value="1" class="btn btn-info"><?php echo $text_button_comment ?></button>  <?php if (!$is_login) { echo $text_login_after_button; } ?></div></form>