<?php function ypGetOnlineIp($format=0) {		if(getenv('HTTP_CLIENT_IP') && strcasecmp(getenv('HTTP_CLIENT_IP'), 'unknown')) {			$onlineip = getenv('HTTP_CLIENT_IP');		} elseif(getenv('HTTP_X_FORWARDED_FOR') && strcasecmp(getenv('HTTP_X_FORWARDED_FOR'), 'unknown')) {			$onlineip = getenv('HTTP_X_FORWARDED_FOR');		} elseif(getenv('REMOTE_ADDR') && strcasecmp(getenv('REMOTE_ADDR'), 'unknown')) {			$onlineip = getenv('REMOTE_ADDR');		} elseif(isset($_SERVER['REMOTE_ADDR']) && $_SERVER['REMOTE_ADDR'] && strcasecmp($_SERVER['REMOTE_ADDR'], 'unknown')) {			$onlineip = $_SERVER['REMOTE_ADDR'];		}		preg_match("/[\d\.]{7,15}/", $onlineip, $onlineipmatches);		$return = $onlineipmatches[0] ? $onlineipmatches[0] : 'unknown';	if($format) {		$ips = explode('.', $return);		for($i=0;$i<3;$i++) {			$ips[$i] = intval($ips[$i]);		}		return sprintf('%03d%03d%03d', $ips[0], $ips[1], $ips[2]);	} else {		return $return;	}}function fetchSubstrIp($ip, $length = null) {	if ($length === null OR $length > 3) {		$length = 1;	}	return implode('.', array_slice(explode('.', $ip), 0, 4 - $length));}